INSERT INTO `pro_message` (`id`, `fromid`, `toid`, `state`, `content`, `title`, `ipfrom`, `tokind`, `fromkind`, `time`, `from`, `to`, `批复`) VALUES
(1, '1', '2', '已读', '6月11日，“神舟十号”搭载着三名宇航员成功发射并精确进入预定轨道。这是中国载人天地往返运输的首次应用性飞行，标志着中国载人航天事业进入了快速发展的新阶段。     西安交通大学电信学院承担了“天宫一号”目标飞行器和神舟系列运输飞船的环境控制与生命保障系统的飞控决策支持任务。电信学院项目组开发的决策支持系统属于载人航天七大系统之一的航天员系统，已经在“天宫一号”、“神舟八号”、“神舟九号”发射任务以及交会对接任务中成功运用。     西安交通大学相关负责人表示，该项目组师生近一年来发扬交大人勤奋踏实、精益求精的优良作风，保证“神舟十号”项目如期顺利完成；同时采用了一些智能决策最新技术，经过与航天员系统相关部分多次全面联试和演练，最终交付了一个高质量的交会对接任务决策支持系统，又一次为我国空间实验室的建设作出了贡献。', '西安交大科研成果成功用于“神舟十号”', '127.0.0.1', 'admin', 'unit', '2013-06-16 14:35', '西安交通大学', '超级管理员', ''),
(2, '1', '1', '已读', '6月13日，西安交大新闻宣传工作会议在科学馆101召开，会议展示了一年来各单位新闻宣传工作的新进展、新成果，深化了学校新闻宣传工作要点，对进一步利用新思维、新技术、新方法、新平台推进学校宣传工作提出了明确要求。校党委书记王建华、常务副书记王小力、副书记赵昌昌出席会议，离退休老同志代表、各分党委（总支）书记、各职能单位负责人及驻站记者、学生代表等近200人参加会议。会议由党委副书记宫辉主持。', '西安交大2013年新闻宣传工作会议召开', '127.0.0.1', 'admin', 'unit', '2014-05-06 14:15', '西安交通大学', '超级管理员', ''),
(3, '1', '1', '已读', '6月13日，西安交大新闻宣传工作会议在科学馆101召开，会议展示了一年来各单位新闻宣传工作的新进展、新成果，深化了学校新闻宣传工作要点，对进一步利用新思维、新技术、新方法、新平台推进学校宣传工作提出了明确要求。校党委书记王建华、常务副书记王小力、副书记赵昌昌出席会议，离退休老同志代表、各分党委（总支）书记、各职能单位负责人及驻站记者、学生代表等近200人参加会议。会议由党委副书记宫辉主持。', '西安交大2013年新闻宣传工作会议召开', '127.0.0.1', 'admin', 'unit', '2014-05-06 14:15', '超级管理员', '西安交通大学', ''),
(8, '1', '1', '已读', '测试邮件', '测试超级管理员接收的邮件', '127.0.0.1', 'root', 'unit', '2013-06-16 02:25:16', '西安交通大学', '超级管理员', ''),
